import Link from 'next/link'
import { useState, useEffect } from 'react'
import Router from 'next/router'
import dynamic from 'next/dynamic'
import { withRouter } from 'next/router'
import { getCookie, isAuth } from '../../actions/auth'
import { getCategories } from '../../actions/category'
import { getTags } from '../../actions/tag'
import '../../node_modules/react-quill/dist/quill.snow.css'
const ReactQuill = dynamic(()=> import('react-quill'), {ssr: false});
const BlogCreate = ({router}) => {
    const [categories, setCategories] = useState([])
    const [tags, setTags] = useState([])
    const [body, setBody] = useState({})
    const [values, setValues] = useState({
        error: '',
        sizeError: '',
        success: '',
        formData: '',
        title: '',
        hidePublishButton: false
    })
    const initCategories = () => {
        getCategories()
            .then(data => setCategories(data))
            .catch(err => setValues({...values, error: err}))
    }
    const initTags = () => {
        getTags()
            .then(data => setTags(data))
            .catch(err => setValues({...values, error: err}))
    }
    const { error, sizeError, success, formData, title, hidePublishButton } = values;
    const getBlogLocalStorage = () => {
        if(typeof window !== 'undefined') {
            if(localStorage && localStorage.getItem('blog')) {
                setBody(JSON.parse(localStorage.getItem('blog')))
            } else {
                return false;
            }
        }
    }
    useEffect(() => {
        setValues({
            ...values,
            formData: new FormData(),
        })
        getBlogLocalStorage()
        initTags()
        initCategories()
    }, [router])
    const handleSubmit = e => {                
        e.preventDefault()
    }
    const handleChange = name => e => {
        const value = name === 'photo' ? e.target.file[0] : e.target.value
        formData.set(name, value)
        setValues({
            ...values,
            [name]: value,
            formData,
            error:''
        })
    }
    const handleBody =  e => {
        setBody(e)
        formData.set('body', e);
        if(localStorage) {
            localStorage.setItem('blog', JSON.stringify(e))
        }
    }
    const createBlogForm = () => {
        return(
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input 
                    type="text" 
                    name="title"
                    value={title}
                    placeholder="Enter title"
                    className="form-control"
                    onChange={handleChange('title')}
                    />
                </div>
                <div className="form-group">
                    <ReactQuill
                    modules={BlogCreate.modules}
                    formats={BlogCreate.formats}
                    value={body}
                    placeholder="Type songthing"
                    onChange={handleBody}
                    />
                </div>
                <div className="form-group">
                    <button type="submit" className="btn btn-primary">
                        Publish
                    </button>
                </div>
            </form>
        )
    }
    return(
        <>
            <div className="row">
                <div className="col-md-8">
                    {createBlogForm()}
                </div>
            </div>
            {JSON.stringify(categories)}
            {JSON.stringify(tags)}
        </>
    )
}
export default withRouter(BlogCreate);

BlogCreate.modules = {
    toolbar: [
        [{ header: '1' }, { header: '2' }, { header: [3, 4, 5, 6] }, { font: [] }],
        [{ size: [] }],
        ['bold', 'italic', 'underline', 'strike', 'blockquote'],
        [{ list: 'ordered' }, { list: 'bullet' }],
        ['link', 'image', 'video'],
        ['clean'],
        ['code-block']
    ]
};
BlogCreate.formats = [
    'header',
    'font',
    'size',
    'bold',
    'italic',
    'underline',
    'strike',
    'blockquote',
    'list',
    'bullet',
    'link',
    'image',
    'video',
    'code-block'
];
