import { useState } from 'react'
import { signin } from '../../actions/auth'
import Link from 'next/link'
const style = {
    padding: '40px'
}
const SignInComponent = () => {
    const [values, setValues] = useState({
        email: '',
        password: '',
        error: null,
        loading: false,
        message: null,
        showForm: true
    })
    const {name, email, password, error, loading, message, showForm } = values;
    const handleSubmit = (e) => {
        e.preventDefault();
        setValues({
            ...values,
            loading: true,
            error: false
        })
        const user = {email, name, password}
        signin(user)
            .then(data=> {
                console.log(data)
                if(data.error) {
                    setValues({
                        ...values, 
                        error: data.error,
                        loading: false
                    })
                } else {
                    setValues({
                        ...values,
                        name: '',
                        email: '',
                        password: '',
                        error: null,
                        message: data.msg,
                        showForm: false
                    })
                }
            })
    }
    const handleChange = name => e => {
        setValues({
            ...values,
            error: false,
            [name]: e.target.value
        })
    }
    const showError = () => (error ? <div style={style}><p className="alert alert-danger">{error}</p></div> : '')
    const showMessage = () => (message ? <div style={style}><p className="alert alert-success">{message}</p></div> : '')
    return (
        <>
        {showError()}
        {showMessage()}
        {showForm && <form onSubmit={handleSubmit} style={style}>
            <div className="form-group">
                <input 
                type="text" 
                className="form-control"
                name="email"
                value={email}
                placeholder="Enter email"
                onChange={handleChange('email')}
                />
            </div>
            <div className="form-group">
                <input 
                type="password" 
                className="form-control"
                name="password"
                value={password}
                placeholder="Enter password"
                onChange={handleChange('password')}
                />
            </div>
            <div className="form-group">
                <input 
                disabled={loading ? true : false}
                type="submit" 
                className="form-control bg-primary"
                style={{color: '#fff'}}
                value="Sign In"
                />
            </div>
            <p className="text-center">
                You don't have account ? <Link href='/signup'><a>Register</a></Link>
            </p>
        </form> }
    </>
    )
}

export default SignInComponent;
