import { useState } from 'react'
import Head from 'next/head'
import Link from 'next/link'
import { withRouter } from 'next/router'
import Layout from '../../components/layout'
import { getBlog } from '../../actions/blog'
import { API, DOMAIN, APP_NAME, FP_APP_ID} from '../../config'
import renderHTML from 'react-render-html'
import moment from 'moment'

const Blog = ({blog}) => {
    const showBlogCategories = () => {
        return blog.categories && blog.categories.map((c,i) => {
            return(
                <Link key={i} href={`/categories/${c.slug}`}>
                    <a className="mr-1 ml-1 mt-3 mb-3">{c.name}</a>
                </Link>
            )
        })
    }
    const showBlogTags = () => {
        return blog.tags && blog.tags.map((t,i)=> {
            return(
                <Link key={i} href={`/categories/${t.slug}`}>
                    <a className="mr-1 ml-1 mt-3 mb-3">{c.name}</a>
                </Link>
            )
        })
    }
    return(
        <>
            <Layout>
                <main>
                    <article>
                        <div className="container-fluid">
                            <section>
                                <div className="row">
                                    <div className="featured-image">
                                    <img src={`${API}/posts/photo/${blog.slug}`} alt={blog.title}/>
                                    </div>
                                </div>
                            </section>
                            <header>
                                        <h2 className="pt-3 pb-3 font-weight-bold">
                                            {blog.title}
                                        </h2>
                            </header>
                            <section>
                                <p className="mark ml-3 pt-2 pb-2">
                                    Posted by {blog.author.name} | Published{" "}
                                    {moment(blog.updateAt).fromNow()}
                                </p>
                            </section>
                            <footer>
                                <p>{showBlogCategories()}</p>
                                <p>{showBlogTags()}</p>
                            </footer>
                        </div>
                    </article>
                </main>
            </Layout>
        </>
    )
}
Blog.getInitialProps =({ query }) => {
    return getBlog(query.slug)
                .then(data=> {
                    if(data.error) {
                        console.log(data.error);
                    } else {
                        return {
                            blog: data
                        }
                    }

                })
}
export default Blog
