import { useState } from 'react'
import Head from 'next/head'
import Link from 'next/link'
import Layout from '../../components/layout'
import { getAll } from '../../actions/blog'
import { DOMAIN, APP_NAME} from '../../config'
import Card from '../../components/blog/Card'
const Blogs = ({blogs, categories, tags, size}) => {
    const head = () => {
        return(
            <head>
                <title>Digital Marketing Blog | {APP_NAME}</title>
                <meta name="description" content="Kiến thức về digital marketing, đầu tư, kinh tế và review sách"/>
                <link rel="canonical" href={`${DOMAIN}${router.pathname}`} />
                <meta property="og:title" content={`Kiến thức mới nhất về Digital Marketing || ${APP_NAME}`} />
            </head>
        )
    }
    const showAllBlog = ()=> {
        return blogs.map((blog,i)=> {
            return(
                <article key={i}>
                    <Card blog={blog} key={i} />
                </article>
            )
        })
    }
    return(
        <>
            <Layout>
                <main>
                    <div className="container-fluid">
                        <header>
                            <div className="col-md-12 pt-3">
                                <h1 className="dislay-4 font-weight-bold">
                                    Hello
                                </h1>
                            </div>
                            <section>
                            <p>Categories and tag</p>
                        </section>
                        </header>
                    </div>
                    <div className="container-fluid">
                        <div className="row">
                            <div className="col-md-12">
                                {showAllBlog()}
                            </div>
                        </div>
                    </div>
                </main>
            </Layout>
        </>
    )
}

Blogs.getInitialProps = () => {
    return getAll().then(data=> {
        if(data.error) {
            console.log(data.error);
        } else {
            return {
                blogs: data.blogs,
                categories: data.categories,
                tags: data.tags,
                size: data.size,
            }
        }
    })
}

export default Blogs
