import { useState, useEffect } from 'react'
import dynamic from 'next/dynamic'
import { withRouter } from 'next/router'
import { getCookie} from '../../actions/auth'
import { getCategories } from '../../actions/category'
import { getTags } from '../../actions/tag'
import { create } from '../../actions/blog'
import '../../node_modules/react-quill/dist/quill.snow.css'
const ReactQuill = dynamic(()=> import('react-quill'), {ssr: false});

const BlogCreate = ({router}) => {
    const [categories, setCategories] = useState([])
    const [tags, setTags] = useState([])
    const [checked, setChecked] = useState([])
    const [checkedTag, setCheckedTag] = useState([])
    const [body, setBody] = useState('')
    const [values, setValues] = useState({
        error: '',
        sizeError: '',
        success: '',
        formData: '',
        title: '',
        hidePublishButton: false
    })
    const token = getCookie('token')
    const initCategories = () => {
        getCategories()
            .then(data => setCategories(data))
            .catch(err => setValues({...values, error: err}))
    }
    const initTags = () => {
        getTags()
            .then(data => setTags(data))
            .catch(err => setValues({...values, error: err}))
    }
    const { error, success, formData, title} = values;
    const getBlogLocalStorage = () => {
        if(typeof window !== 'undefined') {
            if(localStorage && localStorage.getItem('blog')) {
                setBody(JSON.parse(localStorage.getItem('blog')))
            } else {
                return false;
            }
        }
    }
    useEffect(() => {
        setValues({
            ...values,
            formData: new FormData(),
        })
        getBlogLocalStorage()
        initTags()
        initCategories()
    }, [router])
    const handleSubmit = e => {                
        e.preventDefault()
        console.log(formData);
        // create(formData, token)
        //     .then(data => {
        //         setValues({
        //             ...values,
        //             error:'',
        //             success: "Publish success"
        //         })
        //         setBody('')
        //         setCheckedTag('')
        //         setChecked('')
        //     })
        //     .catch(err => setValues({...values, error: err}))
    }
    const handleChange = name => e => {
        const value = name === 'photo' ? e.target.files[0] : e.target.value
        formData.set(name, value)
        setValues({
            ...values,
            [name]: value,
            formData,
            error:''
        })
    }
    const handleBody =  e => {
        setBody(e)
        formData.set('body', e);
        console.log(formData);
        if(localStorage) {
            localStorage.setItem('blog', JSON.stringify(e))
        }
    }
    const createBlogForm = () => {
        return(
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input 
                    type="text" 
                    name="title"
                    value={title}
                    placeholder="Enter title"
                    className="form-control"
                    onChange={handleChange('title')}
                    />
                </div>
                <div className="form-group">
                    <ReactQuill
                    modules={BlogCreate.modules}
                    formats={BlogCreate.formats}
                    value={body}
                    placeholder="Type songthing"
                    onChange={handleBody}
                    />
                </div>
                <div className="form-group">
                    <button type="submit" className="btn btn-primary">
                        Publish
                    </button>
                </div>
            </form>
        )
    }
    const handleToggle = c => () => {
        setValues({
            ...values,
            error: ''
        })
        const clickedCategory = checked.indexOf(c);
        let all = [...checked]
        if(clickedCategory === -1) {
            all.push(c)
        } else {
            all.splice(clickedCategory, 1)
        }
        setChecked(all)
        formData.set('categories', all)
    }
    const handleTagToggle = t => () => {
        setValues({
            ...values,
            error: ''
        })
        const clickedTag = checkedTag.indexOf(t);
        let all = [...checkedTag]
        if(clickedTag === -1) {
            all.push(t)
        } else {
            all.splice(clickedTag, 1)
        }
        setCheckedTag(all)
        formData.set('tags', all)
    }
    const showCategories = () => {
        return (
            categories && categories.map((c,i) => {
                return <li key={i} className="list-unstyled">
                    <input onChange={handleToggle(c._id)} type="checkbox" className="mr-2"/>
                    <label htmlFor="" className="form-check-label">
                        {c.name}
                    </label>
                </li>
            })
        )
    }
    const showTags = () => {
        return (
            tags && tags.map((t,i) => {
                return <li key={i} className="list-unstyled">
                    <input onChange={handleTagToggle(t._id)} type="checkbox" className="mr-2"/>
                    <label htmlFor="" className="form-check-label">
                        {t.name}
                    </label>
                </li>
            })
        )
    }
    return(
        <>
            <div className="row">
                <div className="col-md-8">
                    {createBlogForm()}
                </div>
                <div className="col-md-4">
                    <div className="form-group pb-2">
                        <h5>Featured image</h5>
                        <hr/>
                        <label htmlFor="photo" className="btn btn-outline-info">Upload image</label>
                        <input 
                        type="file" 
                        accept="images/*"
                        id="photo"
                        name="photo"
                        onChange={handleChange('photo')}
                        hidden
                        />
                    </div>
                    <div className="categories-list">
                        <h5>Categories</h5>
                        <hr />
                        <ul style={{
                            maxHeight: '400px',
                            overflowY: 'scroll'
                        }}>
                            {showCategories()}
                        </ul>
                    </div>
                    <div className="tag-list">
                        <h5>Tags</h5>
                        <hr/>
                        <ul style={{
                            maxHeight: '400px',
                            overflowY: 'scroll'
                        }}>
                            {showTags()}
                        </ul>
                    </div>
                </div>
            </div>
        </>
    )
}
export default withRouter(BlogCreate);

BlogCreate.modules = {
    toolbar: [
        [{ header: '1' }, { header: '2' }, { header: [3, 4, 5, 6] }, { font: [] }],
        [{ size: [] }],
        ['bold', 'italic', 'underline', 'strike', 'blockquote'],
        [{ list: 'ordered' }, { list: 'bullet' }],
        ['link', 'image', 'video'],
        ['clean'],
        ['code-block']
    ]
};
BlogCreate.formats = [
    'header',
    'font',
    'size',
    'bold',
    'italic',
    'underline',
    'strike',
    'blockquote',
    'list',
    'bullet',
    'link',
    'image',
    'video',
    'code-block'
];
